// This is a node-js analogue of the index.html demo.  This node.js script
// shall load the module (generated from js-ld) and call a method from it.

require('../sample-js-module').then(function(module) {
  console.log("Value from module: " + module.getAdded());
  console.log("isArrayExport([123]) = " + module.isArrayExport([123]) +
                ", isArrayExport('foo') = " + module.isArrayExport('foo'));
});
